package com.kodnest.javapattern.patternlevel1;

public class pattern22 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=1;i++) {
			for(int j=1;j<=5;j++) {
				System.out.print("*");
			}
			for(int k=1;k<=3;k++) {
				System.out.println();
				System.out.println("*   *");
			}
			for(int l=1;l<=5;l++) {
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
